mapper.select:
		SELECT * FROM(
    		SELECT ROWNUM rnum, tb.* FROM(
        		SELECT pdQCode, userName, pdQSubject, pdQCreated, answerNum, groupNum
        		FROM pQnA
        		
					
						
					
				
        		ORDER BY groupNum DESC, answerNum, pdQCode DESC
     	
    		) tb WHERE ROWNUM <= #{end}      
		) WHERE rnum >= #{start}

		SELECT * FROM(
    		SELECT ROWNUM rnum, tb.* FROM(
        		SELECT pdQCode, userName, pdQSubject, pdQCreated, answerNum, groupNum
        		FROM pQnA
        		
					
						
					
					
						and pdnum=#{pdnum}
					
				
        		ORDER BY groupNum DESC, answerNum, pdQCode DESC
     	
    		) tb WHERE ROWNUM <= #{end}      
		) WHERE rnum >= #{start}

SELECT pdQCode, p.userId, p.userName, pdQSubject, pdQContent, p.pdQCreated, pdNum, answerNum, groupNum
FROM pqna p JOIN member1 m ON p.userId=m.userId
WHERE pdQCode = #{pdQCode}
