mapper.select:
SELECT p.pdnum pdnum, imgOriginalFilename, imgSaveFilename, pdName, pdSumContent, pdPrice, pdcontent content, pdNutrient, pdStMethod, pdType, pdRawName, pdArea, pdDelRule, pdCreated, pdstCount, pdExDate FROM productList p Left OUTER Join Temporary t ON p.pdnum=t.pdnum Left OUTER JOIN Temporary2 t2 ON p.pdnum=t2.pdnum Left OUTER JOIN pmainimg pi ON p.pdnum=pi.pdnum Left OUTER JOIN productin pin ON p.pdnum=pin.pdnum
p.pdnum=#{pdnum}
ORDER BY p.pdcreated DESC

mapper.update:
UPDATE productList 
SET 
pdname=#{pdName},
pdContent=#{content},
pdSumContent=#{pdSumContent}

 WHERE pdnum=#{pdnum}

UPDATE PmainImg 
SET 
imgOriginalFilename=#{imgOriginalFilename},
imgSaveFilename=#{imgSaveFilename}

 WHERE pdnum=#{pdnum}
