mapper.select:
SELECT NVL(COUNT(*),0) FROM productList

	
		bclcode=#{code}
	
	
		scicode=#{code}
	
	
		
	
		AND showandhide!=0

SELECT * FROM (SELECT ROWNUM rnum, pdin.* FROM (SELECT pdincode,pdrawprice,pdinnum,TO_CHAR(pdexdate, 'YYYY-MM-DD') pdexdate,TO_CHAR(pdindate, 'YYYY-MM-DD') pdindate,pi.pdnum pdnum,pl.pdname pdname,pi.supplycode,supplyname,imgSaveFilename,milelage FROM productin pi JOIN productList pl ON pi.pdnum=pl.pdnum JOIN supply s ON pi.supplycode=s.supplycode LEFT OUTER JOIN pmainimg pimg ON pl.pdnum = pimg.pdnum
TO_CHAR(pdindate, 'YYYY-MM-DD')>=#{startDate} AND TO_CHAR(pdindate, 'YYYY-MM-DD')<=#{endDate}
TO_CHAR(pdexdate, 'YYYY-MM-DD')>=#{startDate} AND TO_CHAR(pdexdate, 'YYYY-MM-DD')<=#{endDate}
ORDER BY
pdexdate
pdincode
asc
DESC
) pdin WHERE ROWNUM<=#{end}) WHERE rnum>=#{start}

SELECT * FROM (SELECT ROWNUM rnum, pddis.* FROM (SELECT pdDisCode,pdrawprice,pdDisNum,TO_CHAR(pdexdate, 'YYYY-MM-DD') pdexdate,TO_CHAR(pdDisDate, 'YYYY-MM-DD') pdDisDate,pdDisReason,pdDisAmt,pi.pdnum pdnum,pl.pdname pdname,pi.supplycode,supplyname,imgSaveFilename FROM productDis pd JOIN productList pl ON pd.pdnum=pl.pdnum JOIN productIn pi ON pi.pdnum=pl.pdnum JOIN supply s ON pi.supplycode=s.supplycode LEFT OUTER JOIN pmainimg pimg ON pl.pdnum = pimg.pdnum
TO_CHAR(pdDisdate, 'YYYY-MM-DD')>=#{startDate} AND TO_CHAR(pdDisdate, 'YYYY-MM-DD')<=#{endDate}
TO_CHAR(pdexdate, 'YYYY-MM-DD')>=#{startDate} AND TO_CHAR(pdexdate, 'YYYY-MM-DD')<=#{endDate}
ORDER BY
pdexdate
pdDisCode
asc
DESC
) pddis WHERE ROWNUM<=#{end}) WHERE rnum>=#{start}

mapper.update:
UPDATE productList 
SET  
pdStCount=pdStCount+#{pdinnum}
 WHERE pdnum=#{pdnum}

UPDATE productList 
SET  
pdStCount=pdStCount-#{pdDisNum}
 WHERE pdnum=#{pdnum}

UPDATE PmainImg 
SET 
imgOriginalFilename=#{imgOriginalFilename},
imgSaveFilename=#{imgSaveFilename}

 WHERE pdnum=#{pdnum}
