mapper.select:
	
	SELECT * FROM (
		SELECT ROWNUM rnum, tb.* FROM (
			SELECT qna2Code, q.userId, subject, groupNum,
			depth, orderNo, hitCount,
			TO_CHAR(created, 'YYYY-MM-DD') created
			FROM qna2 q
			JOIN member1 m1 ON q.userId = m1.userId
	
		
			
		
	
	ORDER BY qna2Code DESC
		
			) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >=#{start}
